    /*----------------------------------
    GLOBAL VARIABLES
    ----------------------------------*/
    let darkMode = false;

//--| Chart.js Variables |--//
    let chartGridColor = '#6c757d';
    let chart = new Chart($('#hourly-chart'), {
        type: 'line', options: {
            elements: {
                point: {
                    radius: 0
                }
            },
            plugins: {
                legend: {
                    labels: {
                        color: chartGridColor
                    }
                }
            },
            maintainAspectRatio: false,
            animation: false,
            scales: {
                x: {
                    title: {
                        display: true,
                        text: "Time"
                    },
                    grid: {display: false},
                    ticks: {color: chartGridColor}
                },
                y: {
                    title: {
                        display: true,
                        text: "Fahrenheit"
                    },
                    grid: {display: false},
                    beginAtZero: true,
                    max: 90,
                    min: 0,
                    ticks: {
                        color: chartGridColor,
                        stepSize: 10
                    }
                }
            }
        }
    });

//--| Mapbox Variables |--//
    let map = new mapboxgl.Map({
        accessToken: MAPBOX_API_KEY,
        container: 'map',
        style: STARTING_MAPBOX_STYLE,
        center: STARTING_MAPBOX_COORD,
        zoom: STARTING_MAPBOX_ZOOM
    });
    let marker = new mapboxgl.Marker({draggable: true}).setLngLat(STARTING_MAPBOX_COORD).addTo(map);

    /*----------------------------------
    GLOBAL FUNCTIONS
    ----------------------------------*/
    const geocode = (search, token) => fetch(`https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURIComponent(search)}.json?access_token=${token}`)
        .then(res => res.json())
        .then(data => data.features[0].center);
    const moveMarker = (lng, lat) => {
        map.flyTo({zoom: 15, bearing: 0, center: [lng, lat], essential: true});
        getOpenWeatherData(lng, lat);
    }
    const searchbarLocationUpdate = () => {
        let location = $('#searchbar').val();
        geocode(location, MAPBOX_API_KEY).then(coordinates => {
            marker.setLngLat(coordinates);
            moveMarker(coordinates[0], coordinates[1]);
        });
    }
    const updateChart = color => {
        chart.options = {
            elements: {point: {radius: 0}},
            plugins: {legend: {labels: {color}}},
            maintainAspectRatio: false,
            animation: false,
            scales: {
                x: {
                    title: {display: true, text: "Time", color},
                    grid: {display: false},
                    ticks: {color}
                },
                y: {
                    title: {display: true, text: "Fahrenheit", color},
                    grid: {display: false},
                    beginAtZero: true, max: 90, min: 0, ticks: {color, stepSize: 10}
                }
            }
        };
        chart.update();
    }

    /*----------------------------------
    EVENT HANDLERS
    ----------------------------------*/
    $('#search-button').on('click', event => searchbarLocationUpdate());
    $('#searchbar').on('keyup', event => {
        if (event.keyCode === 13) searchbarLocationUpdate();
    });
    marker.on('dragend', () => moveMarker(marker.getLngLat().lng, marker.getLngLat().lat));
